    function pushProductClickToDataLayer(itemProperties) {
        var listName = itemProperties.list;

        var event = {
            'event': 'product-click',
            'ecommerce': {
                'currencyCode': 'USD',
                'click': {
                    'actionField': {
                        'list': listName,
                    },
                    'products': [
                        itemProperties
                    ]
                }
            }
        };

        pushToDataLayer(event);
    }

