var banner = document.querySelector('.ddb-site-banner');
    var bannerIdentifier = banner.getAttribute('data-banner-identifier');
    var BannerCookieName = 'ddbSiteBanner:' + bannerIdentifier;

    initializeBanner(banner);

    function initializeBanner(banner) {
        document.querySelector('.ddb-site-banner--dismiss').addEventListener('click', function() {
            dismissBanner(banner) 
        });

        var links = document.querySelectorAll('.ddb-site-banner a');
        links.forEach(l => {
            l.addEventListener('click', function() {
                pushPromotionClickToDataLayer('', '');
            });
        });

        if (bannerIsDismissed()) {
            hideBanner(banner);
        } else {
            // push promotion details to data layer
            pushPromotionDetailToDataLayer('', '');
        }
    }

    function dismissBanner(banner) {
        setBannerDismissalCookie();
        hideBanner(banner);
        return true;
    }

    function bannerIsDismissed() {
        return document.cookie.indexOf(BannerCookieName) > -1;
    }

    function hideBanner(banner) {
        banner.style.display = 'none';
        repositionSite();
        return true;
    }

    function setBannerDismissalCookie() {
        document.cookie = BannerCookieName + '=true;expires=' + timeTravel(new Date(), 9999) + ';path=/';
        return true;
    }

    function timeTravel(startDate, daysToTravel) {
        return new Date(startDate.setDate(startDate.getDate() + daysToTravel)).toUTCString();
    }

    function repositionSite(position, top) {
        var body = document.querySelector('body');

        body.style.top = (top || 0) + 'px';
        body.style.position = position || 'initial';
    }

